import React, { useContext } from "react";
import { DndProvider, useDrag, useDrop } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import GlobalContext from "../context/GlobalContext";

const ItemType = {
  LABEL: "label",
};

const DraggableLabel = ({ label, index, moveLabel }) => {
  const [, ref] = useDrag({
    type: ItemType.LABEL,
    item: { index },
  });

  const [, drop] = useDrop({
    accept: ItemType.LABEL,
    hover: (item) => {
      if (item.index !== index) {
        moveLabel(item.index, index);
        item.index = index;
      }
    },
  });

  return (
    <div ref={(node) => ref(drop(node))} className="flex items-center mt-3">
      <input
        type="checkbox"
        checked={label.checked}
        onChange={() =>
          updateLabel({ label: label.label, checked: !label.checked })
        }
        className={`form-checkbox h-5 w-5 text-${label.label}-400 rounded focus:ring-0 cursor-pointer`}
      />
      <span className="ml-2 text-gray-700 capitalize">{label.label}</span>
    </div>
  );
};

export default function Labels() {
  const { labels, updateLabel } = useContext(GlobalContext);

  const moveLabel = (fromIndex, toIndex) => {
    const updatedLabels = [...labels];
    const [movedLabel] = updatedLabels.splice(fromIndex, 1);
    updatedLabels.splice(toIndex, 0, movedLabel);
    // Assuming there's a function in the context to update the labels order
    updateLabelOrder(updatedLabels);
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <React.Fragment>
        <p className="text-gray-500 font-bold mt-10">Label</p>
        {labels.map((label, index) => (
          <DraggableLabel
            key={label.label}
            label={label}
            index={index}
            moveLabel={moveLabel}
          />
        ))}
      </React.Fragment>
    </DndProvider>
  );
}
